mapper.select:
		
SELECT * FROM (SELECT Tb.*, ROWNUM rNum FROM (SELECT * FROM board WHERE bname=#{bname}   
 
	AND ${keyField} LIKE '%'||#{keyString}||'%'  

 ORDER BY idx DESC)Tb)WHERE rNum BETWEEN #{start} AND #{end}

mapper.update:
UPDATE board 
	SET	title=#{param1}, contents=#{param2}, attachedfile=#{param3, jdbcType=VARCHAR} 
	WHERE idx=#{param4}

UPDATE board 
	SET	title=#{param1}, contents=#{param2}, id=#{param3}, 
	attachedfile=#{param4, jdbcType=VARCHAR}, 
	visitcount=#{param5}, bname=#{param6}
	WHERE idx=#{param7}
